import csvimport osdef add_links(folder1, folder2, annotation, csv_path):    examples_folder1=os.listdir(folder1)    examples_folder2=os.listdir(folder2)    examples_folder1.remove('.DS_Store')    examples_folder2.remove('.DS_Store')    annotations=[]    for i in range(len(examples_folder1)):        for j in range(len(examples_folder2)):            annotations.append((examples_folder1[i], examples_folder2[j], annotation))    with open(csv_path, "a", newline="") as f:        writer = csv.writer(f)        writer.writerows(annotations)